//react imports
import React from 'react';
import { StyleSheet, Text, TextInput, TouchableOpacity, View, SafeAreaView, KeyboardAvoidingView, Platform, ImageBackground, FlatList } from 'react-native';
import { useNavigation } from '@react-navigation/core';
import AsyncStorage from '@react-native-async-storage/async-storage';

//my imports
import styles from '../../Styles/mainStyle'
import OptionButton from '../../Components/OptionButton/OptionButton';
import { useEffect } from 'react';

interface vaccineType {
    title: string,
    active: boolean
}
export default function Home() {

    const navigation = useNavigation();
    const [username, setusername] = React.useState<string>('');
    const image = { uri: "https://media.gazetadopovo.com.br/viver-bem/2019/02/vacina-filho-pais-antivacina-600x400-38413e10.jpg" }

    async function LoaduserName() {
        const user: string = await AsyncStorage.getItem('@AppCoronaVac:username') || ''
        await setusername(user)
    }
    useEffect(() => {
        LoaduserName();
    }, [])

    function navigateToWelcome() {
        navigation.navigate('Welcome');
    }
    const vaccineTypeList: vaccineType[] = [
        {
            title: '1ª Dose COVID',
            active: false
        },
        {
            title: '2ª Dose COVID',
            active: false
        },
    ]

    const [listVaccine, setLista] = React.useState<vaccineType[]>(vaccineTypeList);


    return (
        // <ImageBackground source={image} style={styles.image}>
        <SafeAreaView style={styles.safearea}>
            <Text>{username.split(' ').slice(0, 1)}</Text>
            <View style={styles.form}>
                <Text style={styles.title}>Se ainda não tomou a vacina, solicite a sua já</Text>
                <TouchableOpacity
                    style={styles.buttonClickMe}
                    activeOpacity={0.7}
                    onPress={navigateToWelcome}
                >
                    <Text style={styles.textButton}>Sair</Text>
                </TouchableOpacity>
                <FlatList
                    data={listVaccine}
                    renderItem={({ item }) => (
                        <OptionButton title={item.title} active onPress={() => { alert('Não pode tomar') }} />
                    )}
                    keyExtractor={(item, index) => index.toString()}
                    showsVerticalScrollIndicator={false}
                    numColumns={2}
                    contentContainerStyle={styles.itemVaccineCSS}
                />
            </View>
        </SafeAreaView>
        // </ImageBackground >
    );
}
