//react imports
import React from 'react';
import { StyleSheet, Text, TextInput, View, SafeAreaView, KeyboardAvoidingView, ImageBackground, Alert } from 'react-native';
import { useNavigation } from '@react-navigation/core';

//my imports
import Loading from '../Components/Loading/Loading';
import styles from '../Styles/mainStyle';
import { CircleButton } from '../Components/Buttons/CircleButton/CircleButton';
import PasswordTextInput from '../Components/TextInputs/PasswordInput/PasswordInput';
import CustomButton from '../Components/Buttons/CustomButton/CustomButton';
import api from '../Services/api'

interface RedefinirSenhaProps {
    email: string,
    senha: string
}

export default function RedefinirSenha() {

    const navigation = useNavigation();
    const image = { uri: "https://media.gazetadopovo.com.br/viver-bem/2019/02/vacina-filho-pais-antivacina-600x400-38413e10.jpg" }

    const [txtSenha, setSenha] = React.useState('');
    const [txtConfirmarSenha, setConfirmarSenha] = React.useState('');
    const [txtEmail, setEmail] = React.useState('');
    const [flLoading, setLoading] = React.useState(false)

    function navigateToWelcome() {
        navigation.goBack();
    }


    if (flLoading) {
        return (<Loading />)
    }

    async function Redefinir() {
        let errors: Array<string> = [];
        let SenhaAlterada: RedefinirSenhaProps = {
            "email": txtEmail,
            "senha": txtSenha,
        }

        if (txtSenha != txtConfirmarSenha) {
            errors.push('\n\nSenhas não coincidem')
        }
        if (txtConfirmarSenha.trim() === '' && txtSenha.trim() != '') {
            errors.push('\n\nÉ obrigatório digitar novamente a senha')
        }

        setLoading(true)
        await api.put(`/paciente/RedefinirSenha`, SenhaAlterada)
            .then(() => {
                setLoading(false);
                Alert.alert('Aviso', 'Senha redefinida');
                navigation.navigate('Login');
            })
            .catch((e) => {
                if (e.response.data != undefined && e.response.data.errors != undefined) {
                    Object.values(e.response.data.errors).map((item: any) => {
                        errors.push(`\n\n${Object.values(item)}`);
                    })
                    Alert.alert('Aviso: Verifique os erros a seguir', `${errors}`)
                }
                else if (e.response.data.Message != null) {
                    Alert.alert('Aviso', `${e.response.data.Message}`)

                }
                setLoading(false)
            })
    }

    return (
        <ImageBackground source={image} style={styles.image}>
            <SafeAreaView style={styles.container}>
                <KeyboardAvoidingView style={styles.container}>
                    <View>
                        <View style={styles.container}>
                            <View style={styles.form}>
                                <Text style={styles.title}>Bem vindo!</Text>
                                <TextInput style={RedefinirStyles.textInput}
                                    placeholder="Digite seu email"
                                    onChangeText={(text) => { setEmail(text); }}
                                    value={txtEmail}
                                >
                                </TextInput>
                                <PasswordTextInput
                                    placeholder="Digite sua nova senha"
                                    onChangeText={(text) => { setSenha(text); }}
                                    value={txtSenha}
                                />
                                <PasswordTextInput
                                    placeholder={'Digite senha novamente'}
                                    onChangeText={(text) => { setConfirmarSenha(text); }}
                                    value={txtConfirmarSenha}
                                />
                                <CustomButton
                                    title={'Redefinir senha'}
                                    onPress={Redefinir}
                                />
                                <View style={{ paddingTop: '25%' }}>
                                    <CircleButton
                                        title={'<'}
                                        onPress={navigateToWelcome}
                                    />
                                </View>
                            </View>
                        </View>
                    </View>
                </KeyboardAvoidingView>
            </SafeAreaView>
        </ImageBackground>
    );
}

const RedefinirStyles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: 'white',
        alignItems: 'center',
        justifyContent: 'center',
    },
    textTitle: {
        color: 'red',
        fontSize: 28,
        marginBottom: 8
    },
    textInput: {
        height: 40,
        backgroundColor: 'white',
        borderColor: 'black',
        borderRadius: 8,
        borderWidth: 1,
        width: 200,
        marginBottom: 16,
        paddingHorizontal: 8
    },
    textInputPassword: {
        height: 40,
        borderWidth: 0,
        width: '70%',
        marginBottom: 16,
        paddingHorizontal: 8
    },
    buttonIn: {
        backgroundColor: 'red',
        borderRadius: 8,
        height: 50,
        width: '70%',
        justifyContent: 'center',
        alignItems: 'center'
    },
    buttonTextIn: {
        color: '#FFF',
        fontSize: 18,
        fontWeight: 'bold'
    },
    passwordContainer: {
        backgroundColor: 'white',
        marginBottom: 16,
        height: 40,
        borderColor: 'black',
        borderRadius: 8,
        borderWidth: 1,
        width: 200,
        flexDirection: 'row',
        justifyContent: 'space-between'
    },
    iconEye: {
        paddingHorizontal: 8,
        marginTop: 6
    }
})
