//react imports
import React from 'react';
import { StyleSheet, Text, TextInput, TouchableOpacity, View, SafeAreaView, KeyboardAvoidingView, Platform, ImageBackground, Alert } from 'react-native';
import { useNavigation } from '@react-navigation/core';

//my imports
import styles from '../../Styles/mainStyle'
import { CircleButton } from '../../Components/Buttons/CircleButton/CircleButton'
import { BackgroundImageURl } from '../../../assets/constants'

import api from '../../Services/api'


interface CadastroProps {
    nome: string,
    email: string,
    senha: string
}

export default function Cadastro() {

    const navigation = useNavigation();
    const [txtNome, setNome] = React.useState('');
    const [txtEmail, setEmail] = React.useState('');
    const [txtSenha, setSenha] = React.useState('');
    const [txtConfirmarSenha, setConfirmarSenha] = React.useState('');
    const image = { uri: BackgroundImageURl }

    async function navigateToLogin() {
        if (txtNome.trim() === '') {
            alert('Campo Nome é obrigatório');
            return;
        }
        if (txtEmail.trim() === '') {
            alert('Campo E-mail é obrigatório');
            return;
        }
        if (txtSenha.trim() === '') {
            alert('Campo senha é obrigatório');
            return;
        }
        if (txtConfirmarSenha.trim() === '') {
            alert('Campo confirmar senha é obrigatório');
            return;
        }
        if (txtConfirmarSenha != txtSenha) {
            alert('Senhas não coincidem');
            return;
        }
        navigation.navigate('Login');
    }

    async function realizaCadastro() {
        const objCadastro: CadastroProps = {
            nome: txtNome,
            email: txtEmail,
            senha: txtSenha
        }
        const response = await api.post(`/paciente/Cadastro`, objCadastro);

    }


    function navigateToWelcome() {
        navigation.navigate('Welcome');
    }

    return (
        <ImageBackground source={image} style={styles.image}>
            <SafeAreaView style={styles.container}>
                <KeyboardAvoidingView style={styles.container}>
                    <View>
                        <View style={styles.container}>
                            <View style={styles.form}>
                                <Text style={styles.title}>Bem vindo!</Text>
                                <TextInput
                                    placeholder="Nome completo"
                                    style={styles.textInput}
                                    onChangeText={(text) => { setNome(text); }}
                                    value={txtNome}
                                ></TextInput>
                                <TextInput
                                    placeholder="E-mail"
                                    style={styles.textInput}
                                    onChangeText={(text) => { setEmail(text); }}
                                    value={txtEmail}
                                ></TextInput>
                                <TextInput
                                    placeholder="Senha"
                                    style={styles.textInput}
                                    onChangeText={(text) => { setSenha(text); }}
                                    value={txtSenha}
                                ></TextInput>
                                <TextInput
                                    placeholder="Confirmar Senha"
                                    style={styles.textInput}
                                    onChangeText={(text) => { setConfirmarSenha(text); }}
                                    value={txtConfirmarSenha}
                                ></TextInput>
                                <TouchableOpacity
                                    style={styles.buttonClickMe}
                                    activeOpacity={0.7}
                                    onPress={navigateToLogin}
                                >
                                    <Text style={styles.textButton}>Cadastrar</Text>
                                </TouchableOpacity>
                                <View style={{ paddingTop: '25%' }}>
                                    <CircleButton
                                        title={'<'}
                                        onPress={navigateToWelcome}
                                    />
                                </View>
                            </View>
                        </View>
                    </View>
                </KeyboardAvoidingView>
            </SafeAreaView>
        </ImageBackground>
    );
}
